import React, { useState, useRef, useEffect } from 'react';
import imageSrc from './woman.jpg';
import Nav from "./navbar";
import Footer from "./footer";

const ChatBot = ({ uid, handleLogout }) => {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const messagesEndRef = useRef(null);

  const NameUser = uid;

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const summarize = async (userInput) => {
    const apiUrl = 'https://api.openai.com/v1/chat/completions';
    const apiKey = process.env.REACT_APP_OPENAI_API_KEY;

    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${apiKey}`
    };

    const data = {
      model: 'gpt-3.5-turbo',
      messages: [
        { role: 'system', content: `My Name is ${NameUser}. Chat with me, as I am your beautiful friend more like Flirting response in portuguese.` },
        { role: 'user', content: userInput }
      ]
    };

    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers,
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        throw new Error('Failed to fetch');
      }

      const result = await response.json();
      return result.choices[0].message.content;
    } catch (error) {
      console.error('Error:', error);
      return 'Sorry, there was an error processing your request.';
    }
  };

  const saveMessage = async (message) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        console.log('Message saved:', message);
        resolve();
      }, 1000);
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputText.trim()) return;

    const userMessage = { text: inputText, isUser: true };
    setMessages([...messages, userMessage]);
    setInputText('');

    await saveMessage(userMessage);

    const botResponse = await summarize(inputText);
    const botMessage = { text: botResponse, isUser: false };
    setMessages((prevMessages) => [...prevMessages, botMessage]);

    await saveMessage(botMessage);
  };

  useEffect(() => {
    const sendInitialMessage = async () => {
      const initialMessage = `You are talking to a beautiful man whose name is ${NameUser}. Chat with me.`;
      const botResponse = await summarize(initialMessage);
      const botMessage = { text: botResponse, isUser: false };
      setMessages([botMessage]);
    };

    sendInitialMessage();
  }, []);

  const styles = {
    container: {
      backgroundColor: '#000',
      color: '#fff',
      padding: '20px',
      borderRadius: '10px',
      boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between',
    },
    chatBox: {
      width: '90%',
      maxWidth: '600px',
      margin: 'auto',
      padding: '1rem',
      backgroundColor: '#e5e7eb',
      borderRadius: '0.5rem',
      boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
      marginTop: '2rem',
      minHeight: '65vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'flex-end',
    },
    message: {
      padding: '10px',
      flex: '1',
      overflowY: 'auto',
    },
    messageItem: {
      marginBottom: '0.5rem',
      display: 'flex',
    },
    messageBubble: {
      maxWidth: '20rem',
      margin: 'auto',
      padding: '0.5rem 1rem',
      borderRadius: '0.5rem',
    },
    userMessage: {
      backgroundColor: '#059669',
      color: '#ffffff',
      alignItems: 'flex-end',
    },
    otherMessage: {
      backgroundColor: '#d1d5db',
      color: '#000000',
    },
    inputContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '1rem',
    },
    inputField: {
      border: '1px solid #9ca3af',
      borderRadius: '0.5rem',
      minWidth: '40vh',
      alignItems: 'center',
      padding: '2vh',
      flex: '1',
    },
    sendButton: {
      padding: '2vh',
      backgroundColor: '#3b82f6',
      color: '#ffffff',
      border: 'none',
      borderRadius: '0.5rem',
      marginLeft: '1vh',
    },
    yourMessage: {
      backgroundColor: '#059669',
      color: '#ffffff',
      marginRight: '0rem',
    },
    theirMessage: {
      backgroundColor: '#d1d5db',
      color: '#000000',
      marginLeft: '0rem',
    },
    imageher: {
      width: '2.5rem',
      height: '2.5rem',
      marginLeft: '0rem',
      justifyContent: 'center',
      alignItems: 'center',
      border: '1px solid #9ca3af',
      borderRadius: '0.5rem',
      margin: '0.2rem',
    },
    messageContainer: {
      marginBottom: 'auto',
    },
    logoutButton: {
      position: 'fixed',
      top: '20px',
      right: '20px',
      padding: '10px',
      backgroundColor: '#dc2626',
      color: '#fff',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
    },
  };

  return (
    <div style={styles.container}>
      <Nav />
      <div style={{ ...styles.chatBox, ...styles.messageContainer }}>
        <div style={styles.message}>
          {messages.map((message, index) => (
            <div key={index} style={{ ...styles.messageItem, justifyContent: message.isUser ? 'flex-end' : 'flex-start' }}>
              {!message.isUser && <img style={styles.imageher} src={imageSrc} alt="Woman profile" />}
              <div style={{ ...styles.messageBubble, ...(message.isUser ? styles.yourMessage : styles.theirMessage) }}>
                {message.text}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
      </div>
      <div style={styles.inputContainer}>
        <form onSubmit={handleSubmit} style={{ display: 'flex', width: '100%', maxWidth: '600px' }}>
          <input
            type="text"
            value={inputText}
            onChange={handleInputChange}
            placeholder="Type your message..."
            style={styles.inputField}
          />
          <button type="submit" style={styles.sendButton}>Send</button>
        </form>
      </div>
      <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
      <Footer />
    </div>
  );
};

export default ChatBot;
