import React, { useState, useEffect } from 'react';
import ChatBot from './ChatBot'; // Import your ChatBot component
import axios from 'axios';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState(''); // State to store user's name
  const [loggedIn, setLoggedIn] = useState(false);
  const [message, setMessage] = useState('');
  const [uid, setUid] = useState(null); // State to store UID

  const API_KEY = process.env.REACT_APP_API_KEY;

  const handleRegister = async () => {
    try {
      const response = await axios.post(
        `https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=${API_KEY}`,
        {
          email: email,
          displayName: name, // Include user's name during registration
          password: password,
          returnSecureToken: true,
        }
      );
      console.log(response.data);
      setLoggedIn(true);
      setMessage('Registration successful!');
      setUid(response.data.localId); // Store UID
    } catch (error) {
      setMessage(error.response.data.error.message);
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post(
        `https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=${API_KEY}`,
        {
          email: email,
          password: password,
          returnSecureToken: true,
        }
      );
      console.log(response.data);
      setLoggedIn(true);
      setMessage('Login successful!');
      setUid(response.data.localId); // Store UID
    } catch (error) {
      setMessage(error.response.data.error.message);
    }
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setMessage('');
    setUid(null); // Clear UID on logout
  };
  const styles = {
    center: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh", // Isso garante que o conteúdo esteja centralizado verticalmente na tela inteira
    },
    formContainer: {
      textAlign: "center", // Isso centraliza o conteúdo horizontalmente dentro do container
    },
    input: {
      padding: "1vh",
      borderRadius: "20px",
      border: "1px solid #ccc",
      outline: "none",
      width: "40vh", // ajuste conforme necessário
    },
  };

  return (
    <div style={styles.center}>
      {!loggedIn ? (
        <div style={styles.formContainer}>
          <h2>Register</h2>
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={styles.input}

          />
          <br />
          <br />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={styles.input}

          />
          <br />
          <br />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={styles.input}

          />
          <br />
          <br />

          <button onClick={handleLogin}>
                Login
          </button>
          <br />
          <br />

        <button onClick={handleRegister}>
            Register
        </button>
        <br />
        <text> dosent had a account? </text>
        </div>
      ) : (
        <ChatBot handleLogout={handleLogout} uid={name} />
      )}
      {message && <p>{message}</p>}
    </div>
  );
}  
export default App;
