import React, { useState, useRef, useEffect } from 'react';
import imageSrc from './woman.jpg'; // Importe seu arquivo de imagem
import Nav from "./navbar";
import Footer from "./footer";

const ChatBot = ({ uid, handleLogout }) => {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');

  const NameUser = uid;

  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const summarize = async (userInput) => {
    const apiUrl = 'https://api.openai.com/v1/chat/completions';
    const apiKey = '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'; // Coloque sua chave de API aqui

    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${apiKey}`
    };

    const data = {
      model: 'gpt-3.5-turbo',
      messages: [
        { role: 'system', content: `My Name is ${NameUser}. Chat with me, as I am your beautiful friend more like Flirting response in portuguese .` },
        { role: 'user', content: userInput }
      ]
    };

    const response = await fetch(apiUrl, {
      method: 'POST',
      headers,
      body: JSON.stringify(data)
    });

    const result = await response.json();
    const summary = result.choices[0].message.content;

    return summary;
  };

  const saveMessage = async (message) => {
    // Simulando o salvamento assíncrono de mensagens, você deve substituir isso pela sua lógica de salvamento real
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        // Salve a mensagem
        console.log('Message saved:', message);
        resolve();
      }, 1000); // Simular atraso de 1 segundo
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputText.trim()) return;

    // Defina a mensagem do usuário no estado
    const userMessage = { text: inputText, isUser: true };
    setMessages([...messages, userMessage]);
    setInputText('');

    // Salve a mensagem do usuário de forma assíncrona
    await saveMessage(userMessage);

    // Chame a função summarize e atualize o estado das mensagens com a resposta
    const botResponse = await summarize(inputText);
    const botMessage = { text: botResponse, isUser: false };
    setMessages((prevMessages) => [...prevMessages, botMessage]);

    // Salve a mensagem do bot de forma assíncrona
    await saveMessage(botMessage);
  };

  useEffect(() => {
    // Função para enviar a mensagem inicial da AI quando o componente for montado
    const sendInitialMessage = async () => {
      const initialMessage = `You are talking to a beautiful man whose name is ${NameUser}. Chat with me`;
      const botResponse = await summarize(initialMessage);
      const botMessage = { text: botResponse, isUser: false };
      setMessages([botMessage]);
      // Salve a mensagem inicial aqui se necessário
    };

    // Chame a função para enviar a mensagem inicial
    sendInitialMessage();
  }, []);

  const styles = {
    container: {
      backgroundColor: '#000',
      color: '#fff',
      padding: '20px',
      borderRadius: '10px',
      boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
      maxWidth: '100%',
      margin: '0 auto',
      minHeight: '100vh',
      boxSizing: 'border-box',
      overflowX: 'hidden',
    },
    chatBox: {
      maxWidth: '100%',
      padding: '1rem',
      backgroundColor: '#e5e7eb',
      borderRadius: '0.5rem',
      boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
      marginTop: "2rem",
      marginBottom: '5rem', // Espaço adicional para o footer
    },
    message: {
      padding: '10px', // Adicionar padding ao contêiner de mensagens
      marginBottom: 50,
    },
    messageItem: {
      marginBottom: '0.5rem',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-start',
    },
    messageBubble: {
      maxWidth: '80%',
      margin: '0.5rem 0',
      padding: '0.5rem 1rem',
      borderRadius: '0.5rem',
    },
    userMessage: {
      backgroundColor: '#059669',
      color: '#ffffff',
      alignSelf: 'flex-end',
    },
    otherMessage: {
      backgroundColor: '#d1d5db',
      color: '#000000',
    },
    inputContainer: {
      position: "fixed",
      bottom: 0,
      left: 0,
      width: '100%',
      padding: '1rem',
      backgroundColor: '#e5e7eb',
      borderTop: '1px solid #ccc',
      display: 'flex',
      alignItems: 'center',
    },
    inputField: {
      flex: 1,
      border: '1px solid #9ca3af',
      borderRadius: '0.5rem',
      padding: '0.5rem',
      marginRight: '1rem',
    },
    sendButton: {
      padding: "0.5rem 1rem",
      backgroundColor: '#3b82f6',
      color: '#ffffff',
      border: 'none',
      borderRadius: '0.5rem',
      cursor: 'pointer',
    },
    logoutButton: {
      position: 'fixed',
      top: '20px',
      right: '20px',
      padding: '10px',
      backgroundColor: '#dc2626',
      color: '#fff',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
    },
    footer: {
      position: 'fixed',
      bottom: 0,
      left: 0,
      width: '100%',
      padding: '1rem',
      backgroundColor: '#f0f0f0',
      borderTop: '1px solid #ccc',
      textAlign: 'center',
    },
  };

  useEffect(() => {
    // Desabilita o comportamento padrão de rolagem
    document.body.style.overflow = 'auto';
    return () => {
      // Reabilita o comportamento padrão de rolagem quando o componente é desmontado
      document.body.style.overflow = 'auto';
    };
  }, []);

  return (
    <div style={styles.container}>
      <Nav />
      <div style={styles.chatBox}>
        <div style={styles.message}>
          {messages.map((message, index) => (
            <div key={index} style={{ ...styles.messageItem, alignItems: message.isUser ? 'flex-end' : 'flex-start' }}>
              {!message.isUser && <img style={styles.imageher} src={imageSrc} alt="Woman profile" />}
              <div style={{ ...styles.messageBubble, ...(message.isUser ? styles.userMessage : styles.otherMessage) }}>
                {message.text}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
      </div>
      <form onSubmit={handleSubmit} style={styles.inputContainer}>
        <input
          type="text"
          value={inputText}
          onChange={handleInputChange}
          placeholder="Type your message..."
          style={styles.inputField}
        />
        <button type="submit" style={styles.sendButton}>Send</button>
      </form>
      <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
      <Footer style={styles.footer} />
    </div>
  );
};

export default ChatBot;
